package com.day3.datatype;

public class DatatyptDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//byte - size 1 byte = 8 bit = -128 to 127
		byte a = -128;
		System.out.println(a);
		
		
		//short - size - 2byte - 16bit = 32,768 to 32,768
		short b = 3000;
		System.out.println(b);
		
		//int - size 4byte = 2,147,483,647 to 2,147,483,147 
		  int c = 12345;
		  System.out.println(c);
		  //long - size 8byte 64bit = -9,223,372,036,854,775,808 to 9,223,372,036,854,775,808,
		  long d = 1234567345234567l;
		  System.out.println(d);
		  
		  byte myNum = 19;  //byte integer
		  short myValue = 10; //short integer
		  int noOfRoom = 200; //integer (whole number)
		  long distanceToSun = 98000000L; // long integer
	}
	

}
