package realoperation;

public class Relation_oper {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int  a,b;
		a = 40;
		b =10;
		System.out.println("a==b =" +(a==b));
		System.out.println("a!=b =" +(a!=b));
		System.out.println("a>b =" +(a>b));
		System.out.println("a<b =" +(a<b));
		System.out.println("a>=b =" +(b>=a));
		System.out.println("a<=b =" +(b<=a));

	}

}
