package sample22;

public class Loopdemo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//declare variable
		int a =35,b=15;
		
		//addition operation
		System.out.println("a + b = " + (a + b));
		
		//subtraction  operator
		System.out.println("a - b = " + (a - b));
		
		//multiplication  operator
		System.out.println("a * b = " + (a * b));
		
		//division  operator
		System.out.println("a / b = " + (a / b));
		
		//modulo operator
		System.out.println("a % b = " + (a % b));

	}

}
