package sample22;

public class Relational_opertors {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//create variable
		int d =24,r=10;
		
		//value of a and b
		System.out.println("d is" + d + "r is" + r);
		
		// ==operator
		System.out.println(d==r);
		
		//!=operator
		System.out.println(d!=r);

		//>operator
				System.out.println(d>r);
				
		//<operator
				System.out.println(d<r);
				
		//>=operator
				System.out.println(d>=r);
				
		//<=operator
				System.out.println(d<=r);
				
				
				
				
				
	}

}
