package operators;

public class Conditional_operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int a,b;
      
      a=10;
      b=20;
      
      b=(a==1)? 30: 40;
      
      System.out.println("Value of b is " +b);
      
      b=(a==10)? 30:40;
      
      System.out.println("Value of b is " +b);
	}

}
