package ComStudygroup;

public class ASSIGNMENT_OPERATERS {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a,b,c;
		a = 30;
		b = 10;
		c = 0;
		c = a + b;
		
		c += a;
		System.out.println(c);
		a -= b;
		System.out.println(a);
		a *= b;
		System.out.println(a);
		a %= b;
		System.out.println(a);
		

	}

}
